query getAds($after: String, $filters: AdsFiltersInput!, $sortBy: AdSearchSortByEnum, $locale: String, $currentUserId: Int, $service: ServiceEnum, $page: Int, $first: Int!) {
  ads(
    first: $first
    after: $after
    filters: $filters
    sortBy: $sortBy
    locale: $locale
    page: $page
  ) {
    count
    countPages
    perPage
    currentPage
    distance
    pageInfo {
      hasNextPage
      hasPreviousPage
      endCursor
      __typename
    }
    edges {
      node {
        score
        ad {
          id
          slug {
            title
            city
            __typename
          }
          title
          content
          type
          status
          createdAt
          updatedAt
          categories {
            service
            category
            __typename
          }
          ... on ApplicantAd {
            experienceAmount
            badges {
              name
              additionalData
              __typename
            }
            employmentTypes {
              id
              careTypes {
                id
                rateTypes {
                  id
                  rate {
                    amount
                    timeUnit
                    commission
                    __typename
                  }
                  __typename
                }
                __typename
              }
              __typename
            }
            tags {
              primaryService
              primaryCategory
              canDoService
              canDoCategory
              canDoTypes
              extraService
              languages {
                code
                __typename
              }
              workingConditions {
                shareRoom
                workWithCoworker
                __typename
              }
              apartmentDetails {
                houseType
                houseSurface
                gardenType
                __typename
              }
              __typename
            }
            __typename
          }
          ... on SitterAd {
            parentAdType
            childminderSlots {
              occupiedSlots {
                available
                estimatedAvailabilityDate
                ageRange
                careTypes
                __typename
              }
              maxAge
              __typename
            }
            __typename
          }
          address {
            latitude
            longitude
            city
            cityKey
            country
            zipCode
            __typename
          }
          user {
            id
            enabled
            baseType
            roles
            photos {
              thumbnail
              large
              extra_large
              __typename
            }
            isVerified
            firstName
            lastName
            threadsConnections(first: 1, filters: {user: $currentUserId, service: $service}) {
              edges {
                node {
                  id
                  __typename
                }
                __typename
              }
              __typename
            }
            ... on Applicant {
              hasAEStatus
              age
              grades {
                count
                average
                edges {
                  node {
                    owner {
                      firstName
                      lastName
                      __typename
                    }
                    content
                    generalGrade
                    experienceName
                    __typename
                  }
                  __typename
                }
                __typename
              }
              options {
                visaExpiryDate
                __typename
              }
              __typename
            }
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
